{
    "minetest.conf": {
        "parser": "file",
        "find": {
            " name = ": " name = {{server.build.env.SERVER_ADMIN_NAME}}",
            " server_name = ": " server_name = {{server.build.env.SERVER_NAME}}",
            " server_description = ": " server_description = {{server.build.env.SERVER_DESC}}",
            " server_address = ": " server_address = {{server.build.env.SERVER_DOMAIN}}",
            " server_url = ": " server_url = {{server.build.env.SERVER_URL}}",
            " server_announce = ": " server_announce = {{server.build.env.SERVER_ANNOUNCE}}",
            " serverlist_url = ": " serverlist_url = {{server.build.env.SERVER_LIST_URL}}",
            " motd = ": " motd = {{server.build.env.SERVER_MOTD}}",
            " max_users = ": " max_users = {{server.build.env.SERVER_MAX_USERS}}",
            " bind_address = ": " bind_address = 0.0.0.0",
            " default_password = ": " default_password = {{server.build.env.SERVER_PASSWORD}}"
        }
    }
}
